    def getData(self):
        self.data, self.address = self.udp_socket.recvfrom(1024)
        if len(self.data) == 530:
            return self.data, self.address
        return self.dummydata, (self.IP, self.port)
        
    def getCurrentUniverse(self, data):
        return data[14] + data[15]

    def parseDMXChannel(self, data):
        self.dmx_channel.clear()
        for i in range(0, 511):
            self.dmx_channel.append(data[18+i])
        return self.dmx_channel
        